import React from "react";
import cx from "classnames";
import PropTypes from "prop-types";

/**
 * This button signals to Main that the Domain of the Day
 *  img should be displayed or taken down.
 *
 *  DomainButton is a button with state storing a boolean, clicked.
 *  Clicked status is passed up to parent component(s).
 *
 * @reactProps {function} onClick - a function that takes a boolean argument
 */
export default class DomainButton extends React.Component {
  state = {};

  /**
   * Flip clicked on state, pass clicked to parent
   */
  onClick = () => {
    let clicked = !this.state.clicked;
    this.props.onClick(clicked);
    this.setState({ clicked })
  }

  /**
   * Renders a button styled logically with clicked deconstructed from state
   */
  render() {
    const { clicked } = this.state;
    return (
      <button onClick={this.onClick} className={cx({clicked},"closed-menu")}>
        <div className="title"><h3>Domain of the Day</h3></div>
      </button>
    );
  }
}

DomainButton.propTypes = {
  onClick: PropTypes.func.isRequired
}

import React from "react";
import cx from "classnames";
import PropTypes from "prop-types";
import Button from "./Button";
import Popup from "./Popup";
import PopupContent from "./PopupContent";
import CloseButton from "./CloseButton";
import FaQuestionCircle from "react-icons/lib/fa/question-circle";


/**
 * FieldMenu is a Menu that can be "open" or not.
 * When closed it appears to be a button. When open, it
 * utilizes a portal to display a div beside it with
 * clickable field-labeled buttons in a style similar to a table.
 *
 * @reactProps {string} title - the label for the button
 * @reactProps {Object} menuData - an object with menu categories, subcategories,
 *                                   names of products and file names
 * @reactProps {function} handleMenuToggle - takes a string (props.title)
 *                                              tells parent the menu was clicked
 * @reactProps {boolean} open - controls the style of the open and closed state
 * @reactProps {function} handleCloseButton - from parent container. ensures
 *                                              close button closes the menu
 * @reactProps {function} handleFieldChange - from Main, update the product selection
 */
export default class FieldMenu extends React.Component {
  /**
   * stores the rectangle for coordinates
   */
  state = {};

  /**
   * Tell parent component that this FieldMenu was toggled.
   *
   */
  handleMenuToggle = () => {
    this.props.handleMenuToggle(this.props.title);
  }


  /**
   * bounding rectangle for portal established
   */
  componentDidMount() {
    this.getStyle();
    window.addEventListener('resize', () => this.getStyle());
    document.addEventListener('scroll', () => this.getStyle());
  }

  /**
   * Set the rectangle object to state. The open menu div
   * has to be positioned relative to this rectangle.
   */
  getStyle = () => {
    const parent = document.getElementById(this.props.title);
    const rect = parent.getBoundingClientRect();
    this.setState({ parent, rect })
  }


  /**
   * When closed, renders a button displaying props.title
   *  Style determined with props.open, deconstructed and
   *  controlling the "open" className via cx.
   * When open, renders the button displaying props.title
   *  and a Popup > div > PopupContent.
   *  PopupContent children is an array of buttons.
   * Rendered components not yet listed are CloseButton and FaQuestionCircle.
   * @return button, div > Popup > div > PopupContent
   */
  render() {
    const { open } = this.props;
    let i = 0;
    let products = [];
    let handleFieldChange = this.props.handleFieldChange;

    if(this.props.menuData != null) {
        let data = this.props.menuData;
        products.push(<CloseButton handleCloseButton={this.props.handleCloseButton} />)
        Object.keys(data).forEach( category => {
          products.push(<div className="category"><div><span/>{category}<a className="help-icon" href={`../help/${data[category].help}`} target="_blank" rel="noopener noreferrer"><FaQuestionCircle/></a></div></div>);
          Object.keys(data[category]).forEach(subcategory => {
            let buttons = [];
            Object.keys(data[category][subcategory]).forEach(product => {
              let value = data[category][subcategory][product]["productFile"];
              let unit = data[category][subcategory][product]["units"];
              buttons.push(
                <Button
                    key={i++}
                    label={product}
                    value={value}
                    onEvent={handleFieldChange}
                    unit={unit}
                    onClick={true}
                />
              );
            })
            if(subcategory !== "help") {
              products.push(<div className="trow">
                  <span className="subcat">{subcategory}:</span>{buttons}</div>);
            }
          })
        });
    }
    return (
      <button className={cx("closed-menu", {open})} id={this.props.title} onClick={this.handleMenuToggle}>
        <h3>{this.props.title}</h3>
        {open && (
          <Popup>
            <div className={cx("field-menu", "opened")}>
              <PopupContent top={this.state.rect.top} left={this.state.rect.right+5}> {products} </PopupContent>
            </div>
          </Popup>
        )}
      </button>
    );
  }
}

FieldMenu.propTypes = {
  title: PropTypes.string,
  menuItems: PropTypes.object,
  handleFieldChange: PropTypes.func,
  handleCloseButton: PropTypes.func,
  handleDateMenuToggle: PropTypes.funcs,
  open: PropTypes.bool
}

import React from 'react';
import cx from 'classnames';
import PropTypes from 'prop-types';

/**
 * A button that is active on mouse over, not click.
 *
 * @reactProps {string} label - the label for the button
 * @reactProps {string} value - the value to pass up to parent on mouseover
 * @reactProps {string} unitType - the unit type to pass up to parent on mouseover
 * @reactProps {function} onEvent - function vehicle for button action
 * @reactProps {fucntion} highlight - optional. function that controls the style
 * @reactProps {boolean} selected - controls conditional style
 */
export default class HoverButton extends React.Component {

  /**
   *  Pass value and unit type up to parents.
   *   If this button has a highlight prop, highlight.
   */
  onEvent = () => {
    const value = this.props.value;
    const unitType = this.props.unit;
    this.props.onEvent(value, unitType); //|| this.props.highlight(value);
    if (this.props.highlight) { this.props.highlight(value) };
  }

  /**
   *  Renders
   *  @return a button styled conditionally on props.selected.
   */
  render() {
    const { selected } = this.props;
      return (
        <button
          className={cx('button',{ selected })}
          onMouseOver={this.onEvent}>
          <span>{this.props.label}</span>
        </button>
      );
    }
}

HoverButton.propTypes = {
  label: PropTypes.string,
  value: PropTypes.string,
  unit: PropTypes.string,
  onEvent: PropTypes.func,
  highlight: PropTypes.func,
  selected: PropTypes.bool
}

import React from "react";
import PropTypes from "prop-types";
import MenuBox from "./MenuBox";
import DataCanvasContainer from "./DataCanvasContainer";
import MemberButtonBox from "./MemberButtonBox";
import error from "./imagenotthere.png";

/**
 * Main stores the processed app state. Renders two MenuBox and a DataCanvasContainer
 */
export default class Main extends React.Component {
  state = this.props.initialState;

  /** Get the source string for the selected image.
   *
   * @param {string} unit - optional, a newly selected unit
   * @return {string} path to the image product that has been selected
   */
  getSource = (unit) => {
    return("../newse_images/"
    //return("http://nssl.noaa.gov/projects/wof/news-e/newse_images/"
    + this.state.selectedDate + "/"
    + this.state.selectedCycle + "/"
    + this.state.selectedField + "_f"
    + (unit || this.state.selectedUnit) + ".png");
  }

  /** do a quick swap of the domainImg if true
   * when the bool is false, put the former product back
   * @param {boolean} bool - weather or not the Domain of the Day button
   * has been selected
   */
  handleDomainOftheDay = (bool) => {

    if(bool) {
      // create a product string based on the selected date
      let product = `NEWSe_domain_${this.state.selectedDate}`
      // update the browser query string
      const newQueryString = `?d=${this.state.selectedDate}&f=NEWSe_domain`;
      window.history.pushState(null, null, newQueryString);
      // create the img element we need to swap with the current one in the DataCanvas
      const domainImg = <img src={`../newse_images/${this.state.selectedDate}/${product}.png`}
      alt="Domain of the Day" onError={(e) => { e.target.src = error; }} />;
      // store the previous unit type so we can reset the unit buttons correctly later in DataCanvasContainer
      let prevUnitType = this.state.selectedFieldUnitType;
      // push to state data about domain and the previous unit type
      this.setState({ selectedFieldUnitType: "full", prevUnitType, domainImg });
    } else {
      const newQueryString = `?d=${this.state.selectedDate}&c=${this.state.selectedCycle}&f=${this.state.selectedField}`;
      const domainImg = <img src={this.getSource()}
      alt="Forecast" onError={(e) => { e.target.src = error; }} />
      window.history.pushState(null, null, newQueryString);
